import React, { useContext, useState } from "react";
import { ImSpoonKnife } from "react-icons/im";
import { BsCartFill } from "react-icons/bs";
import { Modal, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import "./head.css";
import { UserContext } from "../../App";
const Header = () => {
  const [mod, setMod] = useState(false);
  const { count, fries, coke, setCount, setFries, setCoke } = useContext(UserContext);

  return (
    <div>
      <ul>
        <li>
          <ImSpoonKnife className="I" />
        </li>
        <li>
          <a href="/">
            <h5>Food's Restaurant</h5>
          </a>
        </li>
        <li>
          <BsCartFill className="cart" onClick={() => setMod(true)} />
        </li>
      </ul>
      <Modal show={mod}>
        <Modal.Header>Cart Items</Modal.Header>

        <Modal.Body><table>
          <tr>
            <th>Item name</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
          <tr>
           {count !==0  && <td>Hamburger</td>}
           {fries !==0   && <td>Fries</td>}
           {coke && <td>Coke</td>}
          </tr>
          <tr>
            <td>Centro comercial Moctezuma</td>
            <td>Francisco Chang</td>
            <td>Mexico</td>
          </tr>
          <tr>
            <td>Total</td>
            <td></td>
            <td>{coke}</td>
          </tr>
        </table>

        </Modal.Body>

        <Modal.Footer>
          <Link to="/thanku">
            <Button onClick={() => setMod(false)}>Save and Checkout</Button>
          </Link>
          <Button onClick={() => setMod(false)}>Cancel</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};
export default Header;
