import React, { useContext, useMemo, useState } from "react";
import { ImSpoonKnife } from "react-icons/im";
import { BsCartFill } from "react-icons/bs";
import { Modal, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import "./head.css";
import Card from "../card/Card";
import useGetCardData from "../useGetCardData";
import { UserContext } from "../../App";
const Header = (props) => {
  const [mod, setMod] = useState(false);
 const {count, fries, coke,setCount,setFries,setCoke } = useContext(UserContext);

  return (
    <div>
      <ul>
        <li>
          <ImSpoonKnife className="I" />
        </li>
        <li>
          <a href="/">
            <h5>Food's Restaurant</h5>
          </a>
        </li>
        <li>
          <BsCartFill className="cart" onClick={() => setMod(true)} />
        </li>
      </ul>
      <Modal show={mod}>
        <Modal.Header>Thank You</Modal.Header>

        <Modal.Body> {coke}</Modal.Body>
        <Modal.Footer>
          <Link to="/thanku">
            <Button onClick={()=> setMod(false)}>Save and Checkout</Button>
          </Link>
           <Button onClick={()=>setMod(false)}>Cancel</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};
export default Header;
